describe("openOcean calldata decode", () => {
  it("swap from ERC20 to ERC20", () => {
    const res = decodeCalldata(DEX_ENUM.OPENOCEAN, {
      from: "0xf08c90c7f470b640a21dd9b3744eca3d1d16a044",
      to: "",
      data: "",
      value: "0x0",
      chainId: 56,
    });

    const expectResult: DecodeCalldataResult = {
      fromToken: "0x55d398326f99059fF775485246999027B3197955",
      fromTokenAmount: "1000000000000000000000000000000000",
      minReceiveToTokenAmount: "28287149584156770763857",
      toToken: "0x60D01EC2D5E98Ac51C8B4cF84DfCCE98D527c747",
      toTokenReceiver: "0xf08c90c7f470b640a21dd9b3744eca3d1d16a044",
    };
    expect(res).toEqual(expectResult);
  });

  it("swap from NativeToken to ERC20", () => {
    const res = decodeCalldata(DEX_ENUM.OPENOCEAN, {
      from: "0xf08c90c7f470b640a21dd9b3744eca3d1d16a044",
      to: "",
      data: "",
      value: "0x314dc6448d933800000000000000",
      chainId: 56,
    });

    const expectResult: DecodeCalldataResult = {
      fromToken: CHAINS[CHAINS_ENUM.BSC].nativeTokenAddress,
      fromTokenAmount: "1000000000000000000000000000000000",
      minReceiveToTokenAmount: "16956499961440014995495",
      toToken: "0x60D01EC2D5E98Ac51C8B4cF84DfCCE98D527c747",
      toTokenReceiver: "0xf08c90c7f470b640a21dd9b3744eca3d1d16a044",
    };
    expect(res).toEqual(expectResult);
  });

  it("swap from ERC20 to NativeToken", () => {
    const res = decodeCalldata(DEX_ENUM.OPENOCEAN, {
      from: "0xf08c90c7f470b640a21dd9b3744eca3d1d16a044",
      to: "",
      data: "",
      value: "0x0",
      chainId: 56,
    });

    const expectResult: DecodeCalldataResult = {
      fromToken: "0x60D01EC2D5E98Ac51C8B4cF84DfCCE98D527c747",
      fromTokenAmount: "1000000000000000000000000000000000",
      minReceiveToTokenAmount: "107383355878218254",
      toToken: CHAINS[CHAINS_ENUM.BSC].nativeTokenAddress,
      toTokenReceiver: "0xf08c90c7f470b640a21dd9b3744eca3d1d16a044",
    };
    expect(res).toEqual(expectResult);
  });
});
